function showWindow(marker,text) {
	infowindow.setContent(text);
	infowindow.open(map,marker);
}
function showPoly(event,text) { //used for airspaces
	infowindow.close();
	infowindow.setContent(text);
	infowindow.setPosition(event.latLng);

	infowindow.open(map);
}
function showText(id) {
	lat = notams[id].lat;
	lng = notams[id].lng;
	lastRef = "";
	notamsSame = 0;
	text = "";
	veryLong = false;
	textHeight = 150;

	for (i in notams) {

		if(notams[i].lat == lat && notams[i].lng == lng) {
			if((notams[i].hasPoly & 2) && (notams[i].reference != lastRef)) {
				++notamsSame;
				lastRef = notams[i].reference;
				if (notamsSame > 1) {
					text += "<br><hr style=\"color:#848484;background-color:#848484;height:0.5em\" />";
					textHeight = 250;
				}
				if (notams[i].text.length > 500) veryLong = true;
				chgTxt = "";	
			  igTxt = "";

				text += "<b>" + notams[i].reference + ": <em>" + notams[i].meaning + "</em></b>" + chgTxt + "<br>Q) <a target=\"_blank\" href=\"http://notaminfo.com/explain?id=" + i + "\">" + notams[i].qcode + "</a><pre style=\"background-color: #f0f0f0;\">" + notams[i].text + "</pre>" + notams[i].suffix +"<br>" + igTxt; 
			}
		}
	}
	if (notamsSame > 1 || veryLong) text = "<div style = \"height:" + textHeight + "px; overflow-y:auto;\">" + text + "</div>";
	return "<div style = \" \"" + text + "</div>";
}

// Create a marker whose info window displays the NOTAM.

function notam(id, lat, lng, type, reference, meaning, qcode, text, suffix, fl, letter, firstDay, lastDay,  hasPoly, points, strokeColour, strokeOpacity, innerColour, fillOpacity) {
	var marker = new google.maps.Marker({position:new google.maps.LatLng(lat, lng) });

	google.maps.event.addListener(marker, "click", function() {showWindow(this, showText(id));	});
	if (hasPoly) {
		poly = new google.maps.Polyline({ path:  google.maps.geometry.encoding.decodePath(points), strokeColor: strokeColour, strokeWeight: 2, strokeOpacity: strokeOpacity, fillColor: innerColour, fillOpacity: fillOpacity, zIndex: 1001});
		google.maps.event.addListener(poly, "click", function(event,text) {showPoly(event, showText(id));	});
	} else poly = 0;
	thisNotam = [];
	thisNotam.marker = marker;
	thisNotam.notamType = type;
	thisNotam.firstDay = firstDay;
	thisNotam.lastDay = lastDay;
	thisNotam.poly = poly;
	thisNotam.hasPoly = hasPoly;
	thisNotam.fl = fl;
	thisNotam.reference = reference;
	thisNotam.meaning = meaning;
	thisNotam.qcode = qcode;
	thisNotam.text = text;
	thisNotam.suffix = suffix;
	thisNotam.lat = lat;
	thisNotam.lng = lng;
	thisNotam.letter= letter;
	notams[id] = thisNotam;
}

function airspace(airClass, flightLevel, points, strokeWeight, strokeColour, strokeOpacity, fillColour, fillOpacity,name,type,lower,upper,zIndex)
{
	var polygon = new google.maps.Polygon({ strokeColor: strokeColour, strokeWeight: strokeWeight,
	strokeOpacity: strokeOpacity, path: google.maps.geometry.encoding.decodePath(points),
	fillColor:fillColour, fillOpacity:fillOpacity, zIndex: zIndex });
	google.maps.event.addListener(polygon, "click", function(event, text) {showPoly(event, name + "<br>" + type + "<br>" + lower + " to " + upper )});
	as[airSpaces] = [polygon, airClass, flightLevel,0];
	++airSpaces;
}
function setFL() {
	fl = document.getElementById("fl").value;
	setNOTAMs(0);
		setAirspace();
}

function setSites(siteType) { //fs[x] = [object,type,off/on(0/1)]
	showingSites ^= siteType;
	for (i in fs) {
	
		if (fs[i][1] & showingSites) {
			if (!fs[i][2]) {
				fs[i][0].setMap(map);
				fs[i][2] = true;
			}
			continue;
		}

		if (fs[i][2]) {	//turn off
			fs[i][0].setMap();
			fs[i][2] = false;
		}
	}
}
function setSitesOn() {
	for (i in fs) {
		fs[i][0].setMap(map);
		fs[i][2] = true;
	}
}

function site(lat,long,sitetype,text) {
	url = "http://notaminfo.com/files/" + "icons/";
	siteIcon = url +  "smallGreenMarker.png";
	if (sitetype & 16) {
		siteIcon = url +  "smallWhiteMarker.png";
	}
	if (sitetype & 8 || sitetype & 524288) {
		siteIcon = url +  "smallYellowMarker.png";
	}
	if (sitetype & 131072) {
		siteIcon = url +  "smallOrangeMarker.png";
	}
	if (sitetype & 1048576) {
		siteIcon = url +  "smallBlackMarker.png";
	}
	if (sitetype & 8192) {
		siteIcon = url +  "smallRedMarker.png";
	}
	if (sitetype & 32) {
		siteIcon = url +  "smallPurpleMarker.png";
	}
	if (sitetype & 128) {
		siteIcon = url +  "smallBlueMarker.png";
	}
	if (sitetype & 64 || sitetype & 262144) {
		siteIcon = url +  "smallBrownMarker.png";
	}
	if (sitetype & 16384) {
		siteIcon = url +  "smallWhiteMarker.png";
	}
	var image = new google.maps.MarkerImage(siteIcon,
      new google.maps.Size(12, 20),
      new google.maps.Point(5,1),
      new google.maps.Point(6, 20));
	var siteMarker = new google.maps.Marker({position: new google.maps.LatLng(lat, long), icon:siteIcon });
	google.maps.event.addListener(siteMarker, "click", function() { showWindow(this,text);	});
  fs[sites] = [siteMarker, sitetype, false];
	++sites;
}

function setNOTAMs(notamType) {
	showingNOTAMs ^= notamType;
	theDay = document.getElementById("day").value;
	lastLat = 0;
	lastLng = 0;
	firstInSet = -1;
	samePlace = 1;
		for (i in notams) {
		if (!(notams[i].notamType & (showingNOTAMs & 28))) {
			turnOffNOTAM(i);
			continue;
		}
		if (theDay == 0) {
			if ((notams[i].firstDay > 8) || (notams[i].lastDay < 0)) {
				turnOffNOTAM(i);
				continue;
			}
		}
		else {
			if ((theDay < notams[i].firstDay) || (theDay > notams[i].lastDay)) {
				turnOffNOTAM(i);
				continue;
			}
		}

		if ((!(showingNOTAMs & 128)) && (notams[i].notamType & 128)) { //night?
			turnOffNOTAM(i);
			continue;
		}
		if ((!(showingNOTAMs & 131072)) && (notams[i].notamType & 131072)) { //hidden?
			turnOffNOTAM(i);
			continue;
		}

		lat = notams[i].lat;
		lng = notams[i].lng;
		if ((lat == lastLat) && (lng == lastLng)) {
			++samePlace;
			setMark = false;
			if (samePlace > 50) mark=""; else mark = samePlace;
			notams[firstInSet].marker.setIcon("http://notaminfo.com/files/icons/multiple" + mark + ".png");
		}
		else {
			setMark = true;
			samePlace = 1; //1st mkr at this place
			if ((notams[i].notamType & 4) && !(notams[i].notamType & 24)) {
				notams[i].marker.setIcon("http://notaminfo.com/files/icons/aerodrome" + notams[i].letter + ".png");
			}
			else
				notams[i].marker.setIcon("http://notaminfo.com/files/icons/marker" + notams[i].letter + ".png");
			lastLat = lat;
			lastLng = lng;
			firstInSet = i;
		}
		if (notams[i].hasPoly & 2) { //already displayed?
			if ((notams[i].hasPoly & 4) && !setMark) {
				notams[i].marker.setMap(); //remove mkr
				notams[i].hasPoly -= 4;
			}
			if (!(notams[i].hasPoly & 4) && setMark) {
				notams[i].marker.setMap(map); //show mkr
				notams[i].hasPoly |= 4;
			}
		}
		else { //needs to be displayed
			if (notams[i].hasPoly & 1) {	//has poly?
				notams[i].poly.setMap(map); //show poly
			}
			if (setMark) {
				notams[i].marker.setMap(map); //set mkr
				notams[i].hasPoly |= 4;
			}
			notams[i].hasPoly |= 2;
		}
	}
}
function turnOffNOTAM(id) {

	if (notams[id].hasPoly & 2) { //displayed?
		notams[id].marker.setMap();
		if (notams[id].hasPoly & 1) notams[id].poly.setMap();
		notams[id].hasPoly ^= 6; //displayed off, marker off
	}
}
