url += '&l=0';
window.location.assign("http://notaminfo.com/php/kml.php?u=muse91" + url);
}function setRoute() {
	drawRoute();
	for (ndx in points) {
		tp = points[ndx][0];
		if (document.getElementById("showroute").checked) {
			tp.setMap(map);
			document.getElementById("storerouteid").disabled=false;
			document.getElementById("nmiles").disabled=false;
		}
		else {
			document.getElementById("storerouteid").disabled=true;
			document.getElementById("nmiles").disabled=true;
			tp.setMap();
		}
	}
}
function drawRoute() {

	for (ndx = 1; ndx < points.length; ++ndx) {
		fromPoint = points[ndx-1][0];
		toPoint = points[ndx][0];
		fromLat = fromPoint.getPosition().lat();
		fromLng = fromPoint.getPosition().lng();
		toLat = toPoint.getPosition().lat();
		toLng = toPoint.getPosition().lng();
		if (ndx != 0 && ndx != (points.length - 1)) {
			toPoint.setTitle("Drag this marker to turnpoint " + ndx);
		}
		poly = points[ndx][1];
		if (poly) {
			poly.setMap();
		}
		if (document.getElementById("showroute").checked) {
			poly = new google.maps.Polyline({map: map, path: [new google.maps.LatLng(fromLat, fromLng), new google.maps.LatLng(toLat, toLng)], geodesic: true, strokeColor: "#ff00ff", strokeWeight: 3, strokeOpacity: 0.8, fillColor: fillColor, fillOpacity: 0.2, zIndex: 1002});
			//google.maps.event.addListener(poly, "click", function() { insertTurnpoint(this); });
			points[ndx][1] = poly;
		}
	}
	drawNRB();
}
function insertTurnpoint(poly) {
	a = poly.getPath().getAt(0);
	aLat = a.lat();
	aLong = a.lng();
	for (i = 0; i < (points.length-1); ++i) {
		lat = points[i][0].getPosition().lat();
		lng = points[i][0].getPosition().lng();
		if (lat == aLat && lng == aLong) {
			addTurnpoint(i);
		}
	}
}
function addTurnpoint(turnPoint) { //add after this tp
	infowindow.close();
	a = points[turnPoint][0].getPosition();
	b = points[turnPoint+1][0].getPosition();
	newspot = new google.maps.LatLng((a.lat() + b.lat())/2,(a.lng() + b.lng()) /2);
	var image = new google.maps.MarkerImage('http://notaminfo.com/files/icons/routeTurn.png',
     new google.maps.Size(20, 32),
     new google.maps.Point(0,0),
     new google.maps.Point(10, 31));
	tp = new google.maps.Marker({map: map, position: newspot, icon:image, draggable: true });
	google.maps.event.addListener(tp, "dragstart", function() { infowindow.close(); });
	google.maps.event.addListener(tp, "dragend", function() { drawRoute(); });
	google.maps.event.addListener(tp, "click", function() { showWindow(this, showPosition(this)); });
	points.splice(turnPoint+1,0,[tp,null,null]);
	drawRoute();
}
function removeTurnpoint(a,b) {

	for (i = 1; i < points.length; ++i) {
		lat = points[i][0].getPosition().lat();
		lng = points[i][0].getPosition().lng();
		if (lat == a && lng == b) {
			tp = points[i][0];
			infowindow.close(); 
			tp.setMap();
			poly = points[i][1];
			if (poly) poly.setMap();
			poly = points[i][2];
			if (poly) poly.setMap();
			points.splice(i,1);	
			drawRoute();
			break;
		}
	}
}
function showPosition(theMarker) {
	thePoint = theMarker.getPosition();
	latitude = thePoint.lat();
	longitude = thePoint.lng();
	for (tp = 0; tp < points.length; ++tp) {
		latLng = points[tp][0].getPosition();
		if ((latLng.lat() == latitude) && (latLng.lng() == longitude))
			break;
	}
	if (latitude < 0) { 
		dirNS="S";
		lat = -latitude;
	}
	else {
		lat = latitude;
		dirNS="N"; 
	}
	if (longitude < 0) { 
		dirEW="W";
		lng = -longitude;
	}
	else {
		dirEW="E"; 
		lng = longitude;
	}
	degsLat = Math.floor(lat);
	rawMinsLat = (lat - degsLat) * 60;
	minsLat = Math.floor(rawMinsLat);
	rawSecsLat = (rawMinsLat - minsLat) * 60;
	secsLat = Math.round(rawSecsLat,0);
	if (secsLat == 60) { secsLat = 0; minsLat += 1; }
	if (minsLat == 60) { minsLat = 0; degsLat += 1; }

	degsLong = Math.floor(Math.abs(lng));
	rawMinsLong = (Math.abs(lng) - degsLong) * 60;
	minsLong = Math.floor(rawMinsLong);
	rawSecsLong = (rawMinsLong - minsLong) * 60;
	secsLong = Math.round(rawSecsLong,0);
	if (secsLong == 60) { secsLong = 0; minsLong += 1; }
	if (minsLong == 60) { minsLong = 0; degsLong += 1; }
	txt = "<b>Turn point " + tp + ":</b> ";
	if (tp == 0) txt = "<b>Start :</b> ";
	if (tp == (points.length - 1)) txt = "<b>Finish :</b> ";

	txt += degsLat + "&deg; " + minsLat + "' " +  secsLat +  dirNS + " " + degsLong + "&deg; " + minsLong + "' " + secsLong + dirEW + "<br>" + "South <a href=\"javascript: nudgeNS(tp,-1);\">&lt;</a><a href=\"javascript: nudgeNS(tp,-60);\">&lt;</a><a href=\"javascript: nudgeNS(tp,-3600);\">&lt;</a> nudge <a href=\"javascript: nudgeNS(tp,3600);\">&gt;</a><a href=\"javascript: nudgeNS(tp,60);\">&gt;</a><a href=\"javascript: nudgeNS(tp,1);\">&gt;</a> North<br>West <a href=\"javascript: nudgeEW(tp,-1);\">&lt;</a><a href=\"javascript: nudgeEW(tp,-60);\">&lt;</a><a href=\"javascript: nudgeEW(tp,-3600);\">&lt;</a> nudge <a href=\"javascript: nudgeEW(tp,3600);\">&gt;</a><a href=\"javascript: nudgeEW(tp,60);\">&gt;</a><a href=\"javascript: nudgeEW(tp,1);\">&gt;</a> East";	if (tp < (points.length - 1)) {
		if (points.length == 2) txt += "<br>Trip: "; else txt += "<br>Leg " + (tp + 1) + ": ";
		nextPoint = points[tp + 1][0].getPosition();
		heading = google.maps.geometry.spherical.computeHeading(thePoint, nextPoint);
		if (heading < 0) heading += 360;
		metres = google.maps.geometry.spherical.computeDistanceBetween(thePoint, nextPoint);
		nm = metres/1852;
		km = metres / 1000;
		miles = metres/1609.344;
		if (metres < 1000) 
			txt += Math.round(metres) + " metres";
		else 
			txt += Math.round(nm*10)/10 + "nm, (" + Math.round(km*10)/10 + "km, " + Math.round(miles*10)/10 + " miles)";
		txt += "<br>Heading: " + Math.round(heading*10)/10 + "&deg;";
	}
	else {
		txt += "<br>Total trip ";
		metres = 0;
		for (i = 0; i < (points.length - 1); ++i) {
			firstPoint = points[i][0].getPosition();
			secondPoint = points[i+1][0].getPosition();
			metres += google.maps.geometry.spherical.computeDistanceBetween(firstPoint, secondPoint);
		}
		nm = metres/1852;
		km = metres / 1000;
		miles = metres/1609.344;
		if (metres < 1000)
			txt += Math.round(metres) + " metres";
		else 
			txt += Math.round(nm*10)/10 + "nm, (" + Math.round(km*10)/10 + "km, " + Math.round(miles*10)/10 + " miles)";
	}
	if ((tp > 0) && (tp < (points.length - 1))) txt += '<br><a href = "javascript: removeTurnpoint(' + latitude + "," + longitude + ');">Remove this turnpoint</a>';
	if (tp != 0) txt += '<br><a href = "javascript: addTurnpoint(' + (tp - 1) + ');">Add a turnpoint before this one</a>';
	if (tp < (points.length - 1)) txt += '<br><a href = "javascript: addTurnpoint(' + tp + ');">Add a turnpoint after this one</a>';
	return txt;
}
function nudgeNS(tp,dir) {
	nudge(tp,1,dir);
}	
function nudgeEW(tp,dir) {
	nudge(tp,2,dir);
}
function nudge(tp,latOrLng,dir) {
	turnPoint = points[tp][0];
	newLat = turnPoint.getPosition().lat();
	newLng = turnPoint.getPosition().lng();

	if (latOrLng == 1) {
		newLat += 1/dir;
		newLat = Math.round(newLat*dir)/dir;
	}
	else {
		newLng += 1/dir;
		newLng = Math.round(newLng*dir)/dir;
	}
	turnPoint.setPosition(new google.maps.LatLng(newLat,newLng));
	drawRoute();
	showWindow(turnPoint, showPosition(turnPoint));
}	
function doUpdate() {
	if (document.getElementById("showroute").checked) storeRoute();
}
function storeRoute(){

	nrb = map.getZoom();
	nrb += "," + document.getElementById("nmiles").value;
	for (ndx in points) {
		nrb += "," + points[ndx][0].getPosition().lat();
		nrb += "," + points[ndx][0].getPosition().lng();
	}
		nrb += "&u=15161";

	var ajaxRequest;
	
	try{
		// Opera 8.0+, Firefox, Safari
		ajaxRequest = new XMLHttpRequest();
	} catch (e){
		// Internet Exploders
		try{
			ajaxRequest = new ActiveXObject("Msxml2.XMLHTTP");
		} catch (e) {
			try{
				ajaxRequest = new ActiveXObject("Microsoft.XMLHTTP");
			} catch (e){
				alert("Browser not supported!");
				return false;
			}
		}
	}
	ajaxRequest.onreadystatechange = function(){
		if(ajaxRequest.readyState == 4){
			if(ajaxRequest.status == 200){	//HTTP status
					alert(ajaxRequest.responseText);
			}
		}
	}
	ajaxRequest.open("GET", "/php/saveroute.php?nrb=" + nrb, true);
	ajaxRequest.send(null);
}

function drawNRB() {

	nm = document.getElementById("nmiles").value / 2;
	radius = Math.sqrt(2 * (nm * nm));

	for (ndx = 1; ndx < points.length; ++ndx) {
		fromLat = points[ndx-1][0].getPosition().lat();
		fromLng = points[ndx-1][0].getPosition().lng();
		toLat = points[ndx][0].getPosition().lat();
		toLng = points[ndx][0].getPosition().lng();

		latDiff = toLat - fromLat;
		lngDiff = toLng - fromLng;
		lngDiff = Math.cos(deg2rad(toLat)) * lngDiff;

		origalpha = Math.atan(latDiff/lngDiff);
		alpha = Math.atan(latDiff/lngDiff);
		if (alpha < 0) alpha += (2 * Math.PI);

		if (fromLng >= toLng) {
			forty5 = deg2rad(45);
			one35 = deg2rad(135);
		}
		else {
			forty5 = deg2rad(135);
			one35 = deg2rad(45);
		}	

		newLat1 = toLat + ((radius * Math.sin(alpha + one35))/60);
		newLng1 = toLng  + (((radius / Math.cos(deg2rad(toLat))) * Math.cos(alpha + one35))/60);

		newLat2 = toLat + ((radius * Math.sin(alpha - one35))/60);
		newLng2 = toLng  + (((radius / Math.cos(deg2rad(toLat))) * Math.cos(alpha - one35))/60);

		newLat3 = fromLat + ((radius * Math.sin(alpha - forty5))/60);
		newLng3 = fromLng  + (((radius / Math.cos(deg2rad(fromLat))) * Math.cos(alpha  - forty5))/60);

		newLat4 = fromLat + ((radius * Math.sin(alpha + forty5))/60);
		newLng4 = fromLng  + (((radius / Math.cos(deg2rad(fromLat))) * Math.cos(alpha + forty5))/60);
	
		nrbArea = points[ndx][2];
		if (nrbArea) {
			nrbArea.setMap();
		}
		if (document.getElementById("showroute").checked) {
			nrbArea = new google.maps.Polyline({path: [new google.maps.LatLng(newLat1, newLng1),new google.maps.LatLng(newLat2, newLng2),new google.maps.LatLng(newLat3, newLng3),new google.maps.LatLng(newLat4, newLng4),new google.maps.LatLng(newLat1, newLng1)], strokeColor: "#ccccff", strokeWeight: 3, strokeOpacity: 0.8, fillColor: "#ccccff", fillOpacity: 0.1});
			nrbArea.setMap(map);
		}
		points[ndx][2] = nrbArea;
	}
}

function deg2rad(degrees) {
	return ((degrees/180) * Math.PI);
}
